import React, { Component } from 'react'
import { FormGroup, Form, Label, Input, Col, Button, Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from "react-router-dom";
import JumbotronComponent from './JumbotronComponent'
import http from '../shared/common'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import ReactFormInputValidation from "react-form-input-validation";

class DonorLogin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fields: {
        aadhar: '',
        name: '',
        email: '',
        mobile: '',
        gender: '',
        blood: '',
        plasma: '',
        address: '',
        district: '',
        pincode: '',
      },
      errors: {}
    }
    this.form = new ReactFormInputValidation(this);
    this.form.useRules({
        name: "required",
        email: "required|email",
        phone_number: "required|numeric|digits_between:10,12",
    });
  }
  createDonor(e) {
    if(this.state.field.aadhar && this.state.name && this.state.email && this.state.mobile &&
      this.state.gender && this.state.blood && this.state.plasma && this.state.address && this.state.district
      && this.state.pincode){
      e.preventDefault()
      const newDon={
      'aadhar': this.state.fields.aadhar,
      'name': this.state.fields.name,
      'email': this.state.fields.email,
      'mobile': this.state.fields.mobile,
      'gender': this.state.fields.gender,
      'blood': this.state.fields.blood,
      'plasma': this.state.fields.plasma,
      'address': this.state.fields.address,
      'district': this.state.fields.district,
      'pincode': this.state.fields.pincode
      }
      http.post('donors',{newDon})
      .then(response=>{
          toast.success('Registration Successfull')
          this.props.closeCallBack();
      })
        .catch(error=>{
          toast.error('Registration Unsuccessful.')
        })
    }else{
      toast.error('Fill all the deatils Properly')
    }
  };

  render() {
    return (
      <React.Fragment>
        <ToastContainer position="top-right"
                    autoClose={2000}
                    hideProgressBar={false}
                    newestOnTop={false}
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover />
        <JumbotronComponent />
        <Breadcrumb>
          <BreadcrumbItem><Link to="/home">Home</Link></BreadcrumbItem>
          <BreadcrumbItem active>Donor's Registration</BreadcrumbItem>
        </Breadcrumb>
        <div className="row row-content">

          <div className="col-6 offset-3">
          <h3>Donor Registration</h3>
                    <hr/>
            <Form className="form" >
              <FormGroup>
                <Label for="Name">Name
                <Input type="text" name="name" id="name"
                  onChange={
                    (event) => {
                      this.setState({ name: event.target.value });
                    }
                  }
                  placeholder="First Name + Last Name" />
                </Label>
                <label className="error">
                {this.state.errors.name ? this.state.errors.name : ""}
              </label>
              </FormGroup>
              <FormGroup>
                <Label for="aadhar">Aadhar Number
                <Input type="text" name="aadhar" id="aadhar"
                  onChange={
                    (event) => {
                      this.setState({ aadhar: event.target.value });
                    }
                  }
                  placeholder="Enter Aadhar Number" />
                </Label>
              </FormGroup>
              <FormGroup>
                <Label for="exampleEmail">Email
                <Input type="email" name="email" id="exampleEmail"
                  onChange={
                    (event) => {
                      this.setState({ email: event.target.value });
                    }
                  }
                  placeholder="Enter Email Id" />
                </Label>
              </FormGroup>
              <FormGroup>
                <Label for="examplePassword">Mobile Number
                <Input type="tel" name="mobile" id="exampleMobile"
                  minLength={9} maxLength={11}
                  onChange={
                    (event) => {
                      this.setState({ mobile: event.target.value });
                    }
                  } placeholder="Enter Mobile No" />
                </Label>
              </FormGroup>
              <FormGroup>
                <Label for="exampleSelect">Blood group
                <Input type="select" name="select" id="exampleSelect" onChange={
                  (event) => {
                    this.setState({ blood: event.target.value });
                  }
                }>
                  <option value="Select">-----Select-----</option>
                  <option value="A+">A+</option>
                  <option value="A-">A-</option>
                  <option value="B+">B+</option>
                  <option value="B-">B-</option>
                  <option value="AB+">AB+</option>
                  <option value="AB-">AB-</option>
                  <option value="O+">O+</option>
                  <option value="O-">O-</option>

                </Input>
                </Label>
              </FormGroup>
              <FormGroup tag="fieldset">
                <Label>Plasma donor</Label>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="plasmaDonor" value="Yes" onChange={
                      (event) => {
                        this.setState({ plasma: event.target.value });
                      }
                    } />{' '}
                    YES                </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="plasmaDonor" value="No" onChange={
                      (event) => {
                        this.setState({ plasma: event.target.value });
                      }
                    } />{' '}
                    NO                </Label>
                </FormGroup>

              </FormGroup>

              <FormGroup>
                <Label for="exampleText">Address</Label>
                <Input type="textarea" name="text" id="exampleText" onChange={
                  (event) => {
                    this.setState({ address: event.target.value });
                  }
                } />

              </FormGroup>


              <FormGroup>
                <Label for="exampleDistrict">District
                <Input type="select" name="select" id="exampleDistrict" onChange={
                  (event) => {
                    this.setState({ district: event.target.value });
                  }
                }>
                  <option value="Select">-----Select-----</option>
                  <option value="Ahmednagar">    Ahmednagar </option>
                  <option value="Akola">Akola</option>
                  <option value="Amravati">Amravati</option>
                  <option value=" Aurangabad"> Aurangabad</option>
                  <option value=" Beed"> Beed</option>
                  <option value="  Bhandara">  Bhandara</option>
                  <option value="Buldhana">Buldhana</option>
                  <option value="Chandrapur">Chandrapur</option>
                  <option value="Dhule">Dhule</option>
                  <option value="Gadchiroli">Gadchiroli</option>
                  <option value="Gondia">Gondia</option>
                  <option value="Hingoli">Hingoli</option>

                  <option value="Jalgaon">Jalgaon</option>
                  <option value="Jalna">Jalna</option>
                  <option value="Kolhapur">Kolhapur</option>
                  <option value="Latur">Latur</option>
                  <option value="Mumbai City">Mumbai City</option>
                  <option value=" Mumbai Suburban"> Mumbai Suburban</option>
                  <option value=" Nagpur"> Nagpur</option>
                  <option value="Nanded">Nanded</option>
                  <option value="Kolhapur">Kolhapur</option>
                  <option value="Nandurbar">Nandurbar</option>
                  <option value="Nashik">Nashik</option>
                  <option value="Palghar">Palghar</option>
                  <option value="Parbhani">Parbhani</option>
                  <option value="Pune">Pune</option>
                  <option value="Raigad">Raigad</option>
                  <option value="Jalna">Jalna</option>
                  <option value="Kolhapur">Kolhapur</option>
                  <option value="Ratnagiri">Ratnagiri</option>
                  <option value="Sangli">Sangli</option>
                  <option value=" Satara"> Satara</option>
                  <option value="Sindhudurg">Sindhudurg</option>
                  <option value="Solapur">Solapur</option>
                  <option value="Thane">Thane</option>
                  <option value="Wardha">Wardha</option>
                  <option value="Washim">Washim</option>
                  <option value="Yavatmal"> Yavatmal</option>

                </Input>
                </Label>
              </FormGroup>

              <Col md={2}>
                <FormGroup>
                  <Label for="exampleZip">Pincode
                  <Input type="postal" name="zip" id="exampleZip" 
                  onChange={
                    (event) => {
                      this.setState({ pincode: event.target.value });
                    }
                  } /></Label>
                </FormGroup>
              </Col>

              <FormGroup tag="fieldset">
                <legend>Gender</legend>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="genderSelect" value="Female" onChange={
                      (event) => {
                        this.setState({ gender: event.target.value });
                      }
                    } />{' '}

                  Female
                </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="genderSelect" value="Male" onChange={
                      (event) => {
                        this.setState({ gender: event.target.value });
                      }
                    } />{' '}
                    Male                </Label>
                </FormGroup>
                <FormGroup check >
                  <Label check>
                    <Input type="radio" name="genderSelect" value="Other" onChange={
                      (event) => {
                        this.setState({ gender: event.target.value });
                      }
                    } />{' '}
                other                </Label>
                </FormGroup>
              </FormGroup>

              <br></br>
              <Button onClick={(event) => { this.createDonor(event) }}>Submit</Button>
            </Form>

          </div>

        </div>
      </React.Fragment>
    )
  }
}
export default DonorLogin;
