import React, { Component } from 'react';
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import interactionPlugin from "@fullcalendar/interaction";
import { Button,Modal, ModalHeader, ModalBody, FormGroup, Form, Label, Input
} from 'reactstrap';
import http from '.././shared/common'

class DashBoard extends Component{
    constructor(props) {
        super(props);

        this.state = {
            isModalOpen: false,
            title:'',
            date:'',
            events:[],
            isLoaded:false
        };
        this.toggleModal = this.toggleModal.bind(this);
        this.handleLogin = this.handleLogin.bind(this);
        this.formattor = this.formattor.bind(this);
    }
    async componentDidMount(){
        const url="http://localhost:3001/event"
        const response = await fetch(url)
        const data = await response.json()
        this.formattor(data)
        this.setState({isLoaded:true})
        console.log(this.state.events)
    }
    formattor(data){

    }
    handleLogin(event) {
        this.toggleModal();
        http.post('event',{
            'title': this.state.title,
            'date':this.state.date
        })
        .then(response=>{console.log(response)})
        .catch(error=>{
            console.log(error)
        })
        event.preventDefault();

    }
    toggleModal() {
        this.setState({
            isModalOpen: !this.state.isModalOpen
        });
    }
    handleDateClick = (arg) => { // bind with an arrow function
        this.toggleModal();
        this.setState({date:arg.dateStr})
    }
    render(){
        return(
            <div>
                <FullCalendar
                    plugins={[ dayGridPlugin,interactionPlugin ]}
                    dateClick={this.handleDateClick}
                    events={[
                        { title: 'event 1', date: '2020-11-01' },
                        { title: 'event 2', date: '2019-04-02' }
                    ]}
                />
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Event</ModalHeader>
                    <ModalBody>
                        <Form>
                            <FormGroup>
                                <Label htmlFor="title">Title</Label>
                                <Input type="text" id="title" name="title"
                                    onChange={(e)=>{
                                        this.setState({title:e.target.value})
                                    }} />
                            </FormGroup>
                            <Button color="primary" onClick={ (event) =>{
                                    this.handleLogin(event)
                                }}>Add Event</Button>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        )
    }
}
export default DashBoard;
