import React,{Component} from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Media } from 'reactstrap';

class Event extends Component{
    constructor(props){
        super(props)
        this.state={
            Camp:[],
            isLoaded:false
        }
    }
    async componentDidMount(){
        const url="http://localhost:3001/Camps"
        const response = await fetch(url)
        const data = await response.json()
        if(data.length>0){
            this.setState({Camp:data})
            this.setState({isLoaded:true})
            toast.success('Data is Fetched SuccessFully.')
        }
        else
            toast.error('No Data to Display')
    }
    render(){
        let aboutCards = this.state.Camp.map(person => {
            return (
                <Media tag="li">
                    <Media body className="ml-5">
                        <Media heading>{person.name}</Media>
                        <p>{person.location}</p>
                        <p>{person.mob_no}</p>
                    </Media>
                </Media>
            )
        })
        return(
            <div>
                <ToastContainer position="top-right"
                    autoClose={5000}
                    hideProgressBar={false}
                    newestOnTop={false}
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover/>
                <h1>Upcoming Event & BloodDrives</h1>
                {aboutCards}
            </div>
        )
    }
}
export default Event;
