import React,{Component} from 'react'
import JumbotronComponent from './JumbotronComponent';
import { Link } from "react-router-dom";
import { Breadcrumb, BreadcrumbItem, Col,Container,Row,Card, CardImg, CardBody, CardSubtitle, CardText, CardTitle} from "reactstrap";
import AboutCards from './AboutCards'

class About extends Component{
    constructor(){
        super();
        this.state = {
        people : [
        {
        name : "Our motive - save lives",
        desc: "Our aim is to provide faster blood retrievals and donations to reduce overhead of hunting for required blood amount, because a single pint can save three lives, a single gesture can create a million smiles."
        },
        {
        name : "Our services",
        desc: "We will provide blood transfer and donation because ,People live when people give.With the help of increasing technology,making use of it to for people will be acheived through the system."
        },
        {
        name : "Our team",
        desc: "Team will put all the efforts to arrange required blood.It will work in collaboration to reduce shortage of blood,because every drop will help.Team is connected with the large hospitals to help in emergency cases to make avail the blood as per need."
        },
        ]

        }
        }
    render(){
        let aboutCards = this.state.people.map(person => {
            return (

            <Col sm="4">
            <AboutCards person={person} />
            </Col>
            )
            })
        return(
            <React.Fragment>
            <JumbotronComponent/>
            <Breadcrumb>
                <BreadcrumbItem><Link to="/home">Home</Link></BreadcrumbItem>
                <BreadcrumbItem active>About Us</BreadcrumbItem>
            </Breadcrumb>
            <center>
                <article>In day to day life, everything is available to us on our fingertip. Technology has help us to achieve that. Being so advance in technology, 
                    we still face the issues because of which people are losing their lives. Blood shortage is one of the main cause in Medical fatality rate.
                    To avoid this as much as its possible we intended to create a platform where people can help each other whenever its possible to them. 
                    The person who is need for blood will place the request for blood and the available donors on out platform will volunteer for it.
                    This is our humble try to use the wide spread network for the betterment of the society.
                </article>
                </center>
                <Container>
                <Row>
                <div>
            <Card>
                <CardImg top width="100%" src="https://media.istockphoto.com/photos/blood-donor-hands-with-red-heart-and-drop-medical-donation-and-blood-picture-id1185402769?b=1&k=6&m=1185402769&s=170667a&w=0&h=Hi34Nh8D4cSkLrv1mbK3eNHHpKFBd6RlUHGg1o2fsB8="></CardImg>

                <CardBody>
                    <br></br>
                    <br></br>
                    <CardTitle>{this.props.person.name}</CardTitle>
                    <CardText>{this.props.person.desc}</CardText>
                </CardBody>
            </Card>
        </div>
                </Row>
                </Container>
            </React.Fragment>
        )
    }
}
export default About;
