import React,{Component} from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Event extends Component{
    constructor(props){
        super(props)
        this.state={
            Camps:[],
            isLoaded:false
        }
    }
    async componentDidMount(){
        const url="http://localhost:3001/Donation"
        const response = await fetch(url)
        const data = await response.json()
        if(data.length>0){
            this.setState({Camps:data})
            this.setState({isLoaded:true})
            toast.success('Data is Fetched SuccessFully.')
        }
        else
            toast.error('No Data to Display')
    }
    render(){
        return(
            <div>
                <h1>Upcoming Event & BloodDrives</h1>
                <FullCalendar
                    plugins={[ dayGridPlugin,interactionPlugin,listPlugin ]}
                    headerToolbar={{
                        left:'prev,next today',
                        center:'title',
                        right:'dayGridMonth,dayGridWeek'
                    }}
                    dateClick={this.handleDateClick}
                    events={"http://localhost:3001/camps/event"}
                />
            </div>
        )
    }
}
export default Event;
