import React,{ Component } from "react";
import { Button, Col, Form, FormGroup, Input, Label, Row } from "reactstrap";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import http from '../../shared/common'

class NurseEdit extends Component{
    constructor(props){
        super(props);
        this.state = {
            isLoaded    : false,
            nurse       : {},
            nid         : this.props.nid,
            name       : '',
            email       : '',
            phone       : '',
            gender      : '',
            location    : ''
        }
    }
    loadData(){
        if(!this.isLoaded){
            http.get('Nurse/'+this.state.nid, {}).then((response) =>{
                let nurse = response.data;
                this.setState({ isLoaded : true, nurse : nurse[0],
                    fname   : nurse[0].fname,
                    lname   : nurse[0].lname,
                    email   : nurse[0].email,
                    phone   : nurse[0].phone,
                    location: nurse[0].location,
                    gender  : nurse[0].gender,
                });
                console.log(response.data);

            }).catch((error)=>{
                if(error)
                    console.log('aint got any');
            });
        }
    }
    getForm(){
        return(
            <div className="col-md-9">
                <Form>
                <Row className="form-group">
                        <Label htmlFor="email" md={2}>Email</Label>
                        <Col md={10}>
                            <Input type="email"  id="email" name="email"
                                label="Email Address"
                                placeholder="Email"
                                className="form-control"
                                defaultValue={ this.state.nurse.email }
                                onChange={
                                    (e) => {
                                        this.setState({email:e.target.value});
                                    }
                                }
                            />
                        </Col>
                    </Row>
                    {/* <div style={this.getStyles()}> */}
                        <Row className="form-group">
                            <Label htmlFor="fullname" md={2}>Full Name</Label>
                            <Col md={10}>
                                <Input type="text" id="fullname" name="fullname"
                                    placeholder="Full Name"
                                    className="form-control"
                                    onChange={
                                        (e) => {
                                            this.setState({ name : e.target.value })
                                        }
                                    }
                                />
                            </Col>
                        </Row>
                        <Row className="form-group">
                            <Label htmlFor="mobnum" md={2}>Mobile No.</Label>
                            <Col md={10}>
                                <Input type="number" id="mobnum" name="mobnum"
                                    placeholder="Mob. Number"
                                    className="form-control"
                                    onChange={
                                        (e) => {
                                            this.setState({ mob : e.target.value })
                                        }
                                    }
                                />
                            </Col>
                        </Row>
                        <Row className="form-group">
                            <Label htmlFor="joinDate" md={2}>Join Date</Label>
                            <Col md={10}>
                                <Input type="date" rows="3" id="joinDate" name="joinDate"
                                    placeholder="Join Date"
                                    className="form-control"
                                    onChange={
                                        (e) => {
                                            this.setState({ joinDate : e.target.value })
                                        }
                                    }
                                />
                            </Col>
                        </Row>
                        <Row className="form-group">
                            <Label htmlFor="location" md={2}>City</Label>
                            <Col md={10}>
                                <Input  id="location" name="location"
                                    placeholder="Enter City"
                                    className="form-control"
                                    onChange={
                                        (e) => {
                                            this.setState({ address : e.target.value })
                                        }
                                    }
                                />
                            </Col>
                        </Row>
                        <Row className="form-group">
                            <Label htmlFor="genderSelect" md={2}>Gender</Label>
                            <Col md={10}>
                        <FormGroup check>
                        <Label check>
                            <Input type="radio" name="genderSelect" value="Female" onChange={
                            (event) => {
                                this.setState({ gender: event.target.value });
                            }
                            } />{' '}

                        Female
                        </Label>
                        </FormGroup>
                        <FormGroup check>
                        <Label check>
                            <Input type="radio" name="genderSelect" value="Male" onChange={
                            (event) => {
                                this.setState({ gender: event.target.value });
                            }
                            } />{' '}
                            Male                </Label>
                        </FormGroup>
                        <FormGroup check >
                        <Label check>
                            <Input type="radio" name="genderSelect" value="Other" onChange={
                            (event) => {
                                this.setState({ gender: event.target.value });
                            }
                            } />{' '}
                        other                </Label>
                        </FormGroup>
                        </Col>
                    </Row>
                    {/* </div> */}
                    <Row className="form-group">
                        <Col md={{ size: 10, offset: 2 }}>
                            <Button color="primary" onClick={(values) => this.handleSubmit(values)}>
                                Add Nurse
                            </Button>
                        </Col>
                    </Row>
                </Form>
            </div>
        )
    }
    render(){
        return(
            <div className="container">
                <ToastContainer position="top-right"
                    autoClose={2000}
                    hideProgressBar={false}
                    newestOnTop={false}
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover/>
                <div className="col-12 offset-1">
                    <h3>Nurse Registration</h3>
                    <hr/>
                    { this.state.isLoaded ? this.getForm() : this.loadData() }
                </div>
            </div>
        )
    }
}

export default NurseEdit;
