import React,{ Component } from "react";
import { Row,Label,Col,Button,Input ,Form, FormGroup} from "reactstrap";
import http from '../common'
import Notification,{ notify } from "react-notify-toast";
import validator from 'email-validator';
import { Textarea } from "react-materialize";



const hstyle = {
    'display' : 'none'
}

const vstyle = {
    'display' : 'block'
}

class NurseRegistration extends Component{
    constructor(props){
        super(props);
        this.state = {
            // newNurse    : false,
            email       : '',
            name       : '',
            mob       : '',
            address    : '',
            gender      : '',
            joinDate    :  ''
        }
    }
    // checkEmail(emailId){
    //     if(validator.validate(emailId)){

    //         http.get('nurse/check_nurse_email',{
    //             'email' : emailId
    //         }).then((response) => {
    //             alert(response.status)
    //             console.log('dnk,')
    //             if(response.status === 200){
    //                 this.setState({ newNurse : false, email : emailId })
    //                 notify.show('Email already Exists','warning',1000);
    //             }else{
    //                 this.setState({ newNurse : true, email : emailId });
    //                 this.getStyles()
    //             }
    //         }).catch((error) => {
    //             console.log(error);
    //         }); 
    //     }else{
    //         this.setState({ newNurse : false, email : emailId })
    //     }
    // }
    // getStyles(){
    //     if(this.state.newNurse){
    //         return vstyle;
    //     }else{
    //         return hstyle;
    //     }
    // }
    handleSubmit(e){
        e.preventDefault()
        console.log(this.state)
        const newDon={
        'name': this.state.name,
        'email': this.state.email,
        'mobile': this.state.mobile,
        'gender': this.state.gender,
        'blood': this.state.blood,
        'plasma': this.state.plasma,
        'address': this.state.address,
        'district': this.state.district,
        'pincode': this.state.pincode
        }
        http.post('donors',{newDon})
        .then(response=>{
            console.log(response)
        })
        .catch(error=>{
            console.log(error)
        })
    }
    render(){
        return(
            <div className="row row-content">
                <Notification/>
                <div className="col-12 offset-1">
                    <h3>Nurse Registration</h3>
                    <hr/>
                    <div className="col-12 col-md-9">
                        <Form>
                        <Row className="form-group">
                                <Label htmlFor="email" md={2}>Email</Label>
                                <Col md={10}>
                                    <Input type="email"  id="email" name="email"
                                        placeholder="Email"
                                        className="form-control"
                                        onChange={
                                            (e) => {
                                                this.setState({email:e.target.value});
                                            }
                                        }
                                    />
                                </Col>
                            </Row>
                            {/* <div style={this.getStyles()}> */}
                                <Row className="form-group">
                                    <Label htmlFor="fullname" md={2}>First Name</Label>
                                    <Col md={10}>
                                        <Input type="text" id="fullname" name="fullname"
                                            placeholder="Full Name"
                                            className="form-control"
                                            onChange={
                                                (e) => {
                                                    this.setState({ name : e.target.value })
                                                }
                                            }
                                        />
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                    <Label htmlFor="mobnum" md={2}>Mobile No.</Label>
                                    <Col md={10}>
                                        <Input type="number" id="mobnum" name="mobnum"
                                            placeholder="Mob. Number"
                                            className="form-control"
                                            onChange={
                                                (e) => {
                                                    this.setState({ mob : e.target.value })
                                                }
                                            }
                                        />
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                    <Label htmlFor="joinDate" md={2}>Join Date</Label>
                                    <Col md={10}>
                                        <Input type="date" rows="3" id="joinDate" name="joinDate"
                                            placeholder="Join Date"
                                            className="form-control"
                                            onChange={
                                                (e) => {
                                                    this.setState({ joinDate : e.target.value })
                                                }
                                            }
                                        />
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                    <Label htmlFor="location" md={2}>Address</Label>
                                    <Col md={10}>
                                        <textarea rows="3" id="location" name="location"
                                            placeholder="Full Address"
                                            className="form-control"
                                            onChange={
                                                (e) => {
                                                    this.setState({ address : e.target.value })
                                                }
                                            }
                                        />
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                    <Label htmlFor="genderSelect" md={2}>Gender</Label>
                                    <Col md={10}>
                                <FormGroup check>
                                <Label check>
                                    <Input type="radio" name="genderSelect" value="Female" onChange={
                                    (event) => {
                                        this.setState({ gender: event.target.value });
                                    }
                                    } />{' '}

                                Female
                                </Label>
                                </FormGroup>
                                <FormGroup check>
                                <Label check>
                                    <Input type="radio" name="genderSelect" value="Male" onChange={
                                    (event) => {
                                        this.setState({ gender: event.target.value });
                                    }
                                    } />{' '}
                                    Male                </Label>
                                </FormGroup>
                                <FormGroup check >
                                <Label check>
                                    <Input type="radio" name="genderSelect" value="Other" onChange={
                                    (event) => {
                                        this.setState({ gender: event.target.value });
                                    }
                                    } />{' '}
                                other                </Label>
                                </FormGroup>
                                </Col>
                            </Row>
                            {/* </div> */}
                            <Row className="form-group">
                                <Col md={{ size: 10, offset: 2 }}>
                                    <Button color="primary" onClick={(values) => this.handleSubmit(values)}>
                                        Add Nurse
                                    </Button>
                                </Col>
                            </Row>
                        </Form>
                    </div>
                </div>
            </div>
        )
    }
}

export default NurseRegistration;
