import React, { Component } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Button, Col, FormGroup, Input, Label, Row, Form } from 'reactstrap';

class TransferRegistration extends Component {
    constructor(props) {
        super(props);
        this.state = {
            // newNurse    : false,
            hid: '',
            req_date: '',
            qty: '',
            component: '',
            blood: '',
            status: '',
            hospitals:{}
        }
    }
    async componentDidMount(){
        const url="http://localhost:3001/tranfer/hospital"
        const response = await fetch(url)
        const data = await response.json()
        if(data.length>0){
            this.setState({hospitals:data})
            toast.success('Data is Fetched SuccessFully.')
        }
        else
            toast.error('No Data to Display')
    }
    render() {
        return (
            <div className="row row-content">
                <ToastContainer position="top-right"
                    autoClose={5000}
                    hideProgressBar={false}
                    newestOnTop={false}
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover />
                <div className="col-12 offset-1">
                    <h3>Nurse Registration</h3>
                    <hr />
                    <div className="col-12 col-md-9">
                        <Form>
                            <Row className="form-group">
                                <Label htmlFor="hospital" md={2}>Email</Label>
                                <select id="hospital" className="custom-select" onChange={(event) => {
                                    this.checkHID(event.target.value);
                                }}>
                                    <option value="0">Choose Hospital</option>
                                    {
                                        this.state.hospitals.map((hospital, index) => {
                                            return <option value={hospital.hid} label={hospital.name} />
                                        })
                                    }

                                </select>
                            </Row>
                            {/* <div style={this.getStyles()}> */}
                            <Row className="form-group">
                                <Label htmlFor="fullname" md={2}>First Name</Label>
                                <Col md={10}>
                                    <Input type="text" id="fullname" name="fullname"
                                        placeholder="Full Name"
                                        className="form-control"
                                        onChange={
                                            (e) => {
                                                this.setState({ name: e.target.value })
                                            }
                                        }
                                    />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="mobnum" md={2}>Mobile No.</Label>
                                <Col md={10}>
                                    <Input type="number" id="mobnum" name="mobnum"
                                        placeholder="Mob. Number"
                                        className="form-control"
                                        onChange={
                                            (e) => {
                                                this.setState({ mob: e.target.value })
                                            }
                                        }
                                    />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="joinDate" md={2}>Join Date</Label>
                                <Col md={10}>
                                    <Input type="date" rows="3" id="joinDate" name="joinDate"
                                        placeholder="Join Date"
                                        className="form-control"
                                        onChange={
                                            (e) => {
                                                this.setState({ joinDate: e.target.value })
                                            }
                                        }
                                    />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="location" md={2}>City</Label>
                                <Col md={10}>
                                    <Input id="location" name="location"
                                        placeholder="Enter City"
                                        className="form-control"
                                        onChange={
                                            (e) => {
                                                this.setState({ address: e.target.value })
                                            }
                                        }
                                    />
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="genderSelect" md={2}>Gender</Label>
                                <Col md={10}>
                                    <FormGroup check>
                                        <Label check>
                                            <Input type="radio" name="genderSelect" value="Female" onChange={
                                                (event) => {
                                                    this.setState({ gender: event.target.value });
                                                }
                                            } />{' '}

                                Female
                                </Label>
                                    </FormGroup>
                                    <FormGroup check>
                                        <Label check>
                                            <Input type="radio" name="genderSelect" value="Male" onChange={
                                                (event) => {
                                                    this.setState({ gender: event.target.value });
                                                }
                                            } />{' '}
                                    Male                </Label>
                                    </FormGroup>
                                    <FormGroup check >
                                        <Label check>
                                            <Input type="radio" name="genderSelect" value="Other" onChange={
                                                (event) => {
                                                    this.setState({ gender: event.target.value });
                                                }
                                            } />{' '}
                                other                </Label>
                                    </FormGroup>
                                </Col>
                            </Row>
                            {/* </div> */}
                            <Row className="form-group">
                                <Col md={{ size: 10, offset: 2 }}>
                                    <Button color="primary" onClick={(values) => this.handleSubmit(values)}>
                                        Add Nurse
                                    </Button>
                                </Col>
                            </Row>
                        </Form>
                    </div>
                </div>
            </div>
        )
    }
}
export default TransferRegistration;
