import React,{ Component } from "react";
import { Row,Label,Col,Button,Input ,Form, FormGroup} from "reactstrap";
import http from '../common'
import Notification,{ notify } from "react-notify-toast";
import validator from 'email-validator';
import { Textarea } from "react-materialize";



const hstyle = {
    'display' : 'none'
}

const vstyle = {
    'display' : 'block'
}

class HospitalRegistration extends Component{
    constructor(props){
        super(props);
        this.state = {
            // newNurse    : false,
            h_id  : '',
            name       : '',
            mob_no_       : '',
            address    : '',
            district    :  '',
            picode      :  ''
        }
    }
    // checkEmail(emailId){
    //     if(validator.validate(emailId)){

    //         http.get('nurse/check_nurse_email',{
    //             'email' : emailId
    //         }).then((response) => {
    //             alert(response.status)
    //             console.log('dnk,')
    //             if(response.status === 200){
    //                 this.setState({ newNurse : false, email : emailId })
    //                 notify.show('Email already Exists','warning',1000);
    //             }else{
    //                 this.setState({ newNurse : true, email : emailId });
    //                 this.getStyles()
    //             }
    //         }).catch((error) => {
    //             console.log(error);
    //         }); 
    //     }else{
    //         this.setState({ newNurse : false, email : emailId })
    //     }
    // }
    // getStyles(){
    //     if(this.state.newNurse){
    //         return vstyle;
    //     }else{
    //         return hstyle;
    //     }
    // }
    handleSubmit(e){
        e.preventDefault()
        console.log(this.state)
        const newHospital={
        'h_id': this.state.h_id,
        'name': this.state.name,
        'mob_no_': this.state.mob_no_,
        'city': this.state.city,
        'pincode': this.state.pincode,
        'district': this.state.district


        }
        http.post('Hospital',{newHospital})
        .then(response=>{
            console.log(response)
            notify.show('Hospital Added.','success',300)
        })
        .catch(error=>{
            console.log(error)
        })
    }
    render(){
        return(
            <div className="row row-content">
                <Notification/>
                <div className="col-12 offset-1">
                    <h3>Hospital Registration</h3>
                    <hr/>
                    <div className="col-12 col-md-9">
                        <Form>
                        <Row className="form-group">
                                <Label htmlFor="h_id" md={2}>Hospital_id</Label>
                                <Col md={10}>
                                    <Input type="text"  id="h_id" name="h_id"
                                        placeholder="Hospital_id"
                                        className="form-control"
                                        onChange={
                                            (e) => {
                                                this.setState({h_id:e.target.value});
                                            }
                                        }
                                    />
                                </Col>
                            </Row>


                            {/* <div style={this.getStyles()}> */}
                                <Row className="form-group">
                                    <Label htmlFor="name" md={2}>Full Name</Label>
                                    <Col md={10}>
                                        <Input type="text" id="name" name="name"
                                            placeholder="Full Name"
                                            className="form-control"
                                            onChange={
                                                (e) => {
                                                    this.setState({ name : e.target.value })
                                                }
                                            }
                                        />
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                <Label htmlFor="mob_no_" md={2}>Mobile_Number</Label>
                                    <Col md={10}>
                                    <Input type="text" name="mob_no_" id="mob_no_" placeholder="1234567891"
                                    onChange={
                                    (e) => {
                                          this.setState({ mob_no_: e.target.value });
                    }
                                        } />
                                     </Col>
                                </Row>
                                <Row className="form-group">
                                    <Label htmlFor="district" md={2}>District</Label>
                                    <Col md={10}>
                                        <Input  id="district" name="district"
                                            placeholder="Enter district"
                                            className="form-control"
                                            onChange={
                                                (e) => {
                                                    this.setState({ district : e.target.value })
                                                }
                                            }
                                        />
                                    </Col>
                                </Row>

                                <Row className="form-group">
                                    <Label htmlFor="city" md={2}>city</Label>
                                    <Col md={10}>
                                        <Input  id="city" name="city"
                                            placeholder="Enter City"
                                            className="form-control"
                                            onChange={
                                                (e) => {
                                                    this.setState({ city : e.target.value })
                                                }
                                            }
                                        />
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                    <Label htmlFor="pincode" md={2}>Pincode</Label>
                                    <Col md={10}>
                                        <Input  id="Pincode" name="location"
                                            placeholder="Enter pin"
                                            className="form-control"
                                            onChange={
                                                (e) => {
                                                    this.setState({ pincode : e.target.value })
                                                }
                                            }
                                        />
                                    </Col>
                                </Row>





                            {/* </div> */}
                            <Row className="form-group">
                                <Col md={{ size: 10, offset: 2 }}>
                                    <Button color="primary" onClick={(values) => this.handleSubmit(values)}>
                                        Add Hospital
                                    </Button>
                                </Col>
                            </Row>
                        </Form>
                    </div>
                </div>
            </div>
        )
    }
}

export default HospitalRegistration;
