import React,{Component} from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Event extends Component{
    constructor(props){
        super(props)
        this.state={
            Camp:[],
            isLoaded:false
        }
    }
    async componentDidMount(){
        const url="http://localhost:3001/Camps"
        const response = await fetch(url)
        const data = await response.json()
        if(data.length>0){
            this.setState({Camp:data})
            this.setState({isLoaded:true})
            toast.success('Data is Fetched SuccessFully.')
        }
        else
            toast.error('No Data to Display')
    }
    render(){
        let aboutCards = this.state.people.map(person => {
            return (

            <Col sm="4">
            <AboutCards person={person} />
            </Col>
            )
        })
        return(
            <div>
                <h1>Upcoming Event & BloodDrives</h1>

            </div>
        )
    }
}
export default Event;
